import React, { useState } from 'react';
import ClientDetails from '../component/ClientDetails';
import Dates from '../component/Dates';
import Footer from '../component/Footer';
import Header from '../component/Header';
import MainDetails from '../component/MainDetails';
import Notes from '../component/Notes';
import Table from '../component/Table';

const Facture = () => {
    const [showInvoice, setShowInvoice] = useState(false)

    return (
        <div className='page'>

            <div>
                <main className='tw- p-5 xl:max-w-4xl xl:mx-auto bg-white rounded shadow'>
                    {showInvoice ? <div>
                        <Header />
                        <MainDetails />
                        <ClientDetails />
                        <Dates />
                        <Table />
                        <Notes />
                        <Footer />
                    </div> : (
                        <>
                            <div className='tw- felx flex-col justify-center '>

                                <input type="text" name='text' id='text' placeholder='enter your name' autoComplete='off' />
                                <button on className='tw- bg-blue-500 text-white font-bold py-2 px-8 rounded shadow border-2 border-blue-500 hover:bg-transparent hover:text-blue-500 transition-all duration-300'>Preview Invoice</button>
                            </div>
                        </>
                    )
                    }


                </main>
            </div>

        </div>
    );
};

export default Facture;
