import React, { useEffect, useState } from 'react';
import SideMenu from '../component/SideMenu';
import "./Type_dossier.css"; 
import RecetteShow from '../component/InputRecette'; 
import axios from 'axios'


    


const Recette_du_finance = () => {

    const [recette , setRecette] = useState([]) ; 
    const [form, setForm] = useState({});
    //const [errors, setErrors] = useState({});
    const [message, setMessage] = useState("");
    const [show, setShow] = useState(false);
  


    const OnDelete = (id) => {  
        if(window.confirm("Voulez vous vrairement supprimer ce timbre ")) { 
          axios.delete(`/api/recette/${id}`) 
          .then(res=> {  
            setMessage(res.data.message) 
            setShow(true) 
            setTimeout(()=> { 
                setShow(false)
            },4000)
            
          })
        }

    }
    useEffect(()=> {  
         axios.get('/api/recette') 
         .then (res=> {  
            setRecette(res.data)

         });

    },[])


    return (
        <div>
            
            <div className='page'>
            <header id="header">
    <nav>
        <div class="container">
            <div class="text-center">
                <a href="/" class="nav-brand text-dark">Liste des recettes</a>
            </div>
        </div>
    </nav> 
    </header>
           
   <main id="site-main">
       <div class="container">
           <div class="box-nav d-flex justify-between">
               <a href="/AddRecette" class="border-shadow">
                   <span class="text-gradient">Ajouter recette </span>
               </a>
           </div>
          
           <form action="/" method="POST">
               <table class="table">
                   <thead class="thead-dark">
                       <tr> 
                           
                           <th>Libelle</th>
                           <th>Montant</th>  
                           <th>Action</th> 
                       </tr>
                   </thead>
            <tbody>  
              
            {  
                recette.map(({libeller, montantr, _id}) => (
                <RecetteShow libeller={libeller} montant={montantr} ID={_id} OnDelete={OnDelete} />
                ))
             }
    

            </tbody>
               </table>
           </form>
       </div>
   </main>
            </div>
        </div>
    );
};

export default Recette_du_finance;
